import React, { useEffect, useState } from "react";
import { getCustomerRepairRequests, createRepairRequest, updateRepairRequest, deleteRepairRequest } from "../api/api";

const CustomerDashboard = ({ customerId }) => {
  const [requests, setRequests] = useState([]);
  const [editingRequestId, setEditingRequestId] = useState(null);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    username: "",
    email: "",
    contactNumber: "",
    address: "",
    damageType: "",
  });

  const fetchRequests = async () => {
    try {
      const res = await getCustomerRepairRequests(customerId);
      setRequests(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchRequests();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleEdit = (request) => {
    setEditingRequestId(request._id);
    setFormData({
      firstName: request.customerId?.firstName || "",
      lastName: request.customerId?.lastName || "",
      username: request.customerId?.username || "",
      email: request.customerId?.email || "",
      contactNumber: request.customerId?.contactNumber || "",
      address: request.customerId?.address || "",
      damageType: request.damageType || "",
    });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await updateRepairRequest(editingRequestId, {
        damageType: formData.damageType,
      });
      alert("Repair request updated successfully!");
      setEditingRequestId(null);
      fetchRequests();
    } catch (err) {
      console.error(err);
      alert("Failed to update request.");
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this repair request?")) {
      try {
        await deleteRepairRequest(id);
        fetchRequests();
      } catch (err) {
        console.error(err);
        alert("Failed to delete request.");
      }
    }
  };

  const handleDownloadPDF = (id) => {
    window.open(`http://localhost:5000/api/repair-requests/report/download/${id}`, "_blank");
  };

  return (
    <div className="p-8 bg-[#F1F2F7] min-h-screen">
      <h2 className="text-3xl font-bold mb-6 text-[#072679]">My Repair Requests</h2>

      {requests.map((req) => (
        <div key={req._id} className="bg-white shadow-md rounded-lg p-6 mb-6">
          <h3 className="text-xl font-bold text-[#072679] mb-2">Request ID: {req._id}</h3>
          <p><span className="font-semibold text-[#36516C]">Customer Name:</span> {req.customerId?.firstName} {req.customerId?.lastName}</p>
          <p><span className="font-semibold text-[#36516C]">Username:</span> {req.customerId?.username}</p>
          <p><span className="font-semibold text-[#36516C]">Email:</span> {req.customerId?.email}</p>
          <p><span className="font-semibold text-[#36516C]">Contact:</span> {req.customerId?.contactNumber}</p>
          <p><span className="font-semibold text-[#36516C]">Address:</span> {req.customerId?.address}</p>
          <p><span className="font-semibold text-[#36516C]">Damage Type:</span> {req.damageType}</p>
          <p><span className="font-semibold text-[#36516C]">Status:</span> {req.status}</p>
          <p><span className="font-semibold text-[#36516C]">Progress:</span> {req.repairProgress}%</p>
          <p><span className="font-semibold text-[#36516C]">Current Stage:</span> {req.currentStage}</p>

          <div className="flex gap-2 mt-4">
            <button
              onClick={() => handleEdit(req)}
              className="bg-[#42ADF5] hover:bg-[#2C8ED1] text-white font-bold py-1 px-3 rounded"
            >
              Update
            </button>
            <button
              onClick={() => handleDelete(req._id)}
              className="bg-[#D88717] hover:bg-[#B26B13] text-white font-bold py-1 px-3 rounded"
            >
              Delete
            </button>
            <button
              onClick={() => handleDownloadPDF(req._id)}
              className="bg-[#072679] hover:bg-[#041845] text-white font-bold py-1 px-3 rounded"
            >
              Download PDF
            </button>
          </div>
        </div>
      ))}

      {editingRequestId && (
        <div className="bg-white shadow-lg rounded-lg p-6 mt-8">
          <h3 className="text-2xl font-bold mb-4 text-[#072679]">Update Repair Request</h3>
          <form onSubmit={handleUpdate} className="flex flex-col gap-4">
            <div>
              <label className="block font-semibold text-[#36516C] mb-1">Damage Type</label>
              <input
                type="text"
                name="damageType"
                value={formData.damageType}
                onChange={handleChange}
                className="w-full border border-gray-300 rounded px-3 py-2"
                required
              />
            </div>
            <button
              type="submit"
              className="bg-[#42ADF5] hover:bg-[#2C8ED1] text-white font-bold py-2 px-4 rounded mt-2"
            >
              Save Changes
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default CustomerDashboard;
