import React, { useState, useEffect } from "react";
import { getUsers, createRepairRequest } from "../api/api";

const RepairRequestForm = () => {
  const [users, setUsers] = useState([]);
  const [formData, setFormData] = useState({
    customerId: "",
    damageType: ""
  });

  useEffect(() => {
    getUsers().then(res => setUsers(res.data));
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await createRepairRequest(formData);
      alert("Repair request created successfully!");
      console.log(res.data);
    } catch (err) {
      console.error(err);
      alert("Error creating repair request");
    }
  };

  return (
    <div className="max-w-lg mx-auto mt-10 p-6 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold mb-4 text-primary">New Repair Request</h2>
      <form onSubmit={handleSubmit} className="flex flex-col gap-4">
        <div>
          <label className="block mb-1 font-medium">Customer</label>
          <select
            name="customerId"
            value={formData.customerId}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded px-3 py-2"
            required
          >
            <option value="">Select Customer</option>
            {users.map((user) => (
              <option key={user._id} value={user._id}>
                {user.firstName} {user.lastName} ({user.email})
              </option>
            ))}
          </select>
        </div>

        <div>
          <label className="block mb-1 font-medium">Damage Type</label>
          <input
            type="text"
            name="damageType"
            value={formData.damageType}
            onChange={handleChange}
            placeholder="Describe the damage"
            className="w-full border border-gray-300 rounded px-3 py-2"
            required
          />
        </div>

        <button
          type="submit"
          className="bg-secondary hover:bg-[#2C8ED1] text-white font-bold py-2 px-4 rounded"
        >
          Submit Request
        </button>
      </form>
    </div>
  );
};

export default RepairRequestForm;
