import React from 'react';

const RepairCard = ({ repair, onUpdate, onDelete, onDownload }) => {
  return (
    <div className="bg-white p-4 rounded shadow mb-4">
      <h3 className="text-primary font-semibold">Repair ID: {repair._id}</h3>
      <p><span className="font-bold">Customer:</span> {repair.customerId?.username}</p>
      <p><span className="font-bold">Damage Type:</span> {repair.damageType}</p>
      <p><span className="font-bold">Status:</span> {repair.status}</p>
      <div className="mt-2 space-x-2">
        <button
          className="bg-secondary text-white px-3 py-1 rounded hover:bg-hoverBlue"
          onClick={() => onUpdate(repair._id)}
        >
          Update
        </button>
        <button
          className="bg-accent text-white px-3 py-1 rounded"
          onClick={() => onDelete(repair._id)}
        >
          Delete
        </button>
        <button
          className="bg-primary text-white px-3 py-1 rounded"
          onClick={() => onDownload(repair._id)}
        >
          Download Report
        </button>
      </div>
    </div>
  );
};

export default RepairCard;
