import axios from 'axios';

const BASE_URL = 'http://localhost:5000/api';

export const getAllRepairRequests = (params) => axios.get(`${BASE_URL}/repairs`, { params });
export const getRepairById = (id) => axios.get(`${BASE_URL}/repairs/${id}`);
export const createRepairRequest = (data) => axios.post(`${BASE_URL}/repairs`, data);
export const updateRepairRequest = (id, data) => axios.put(`${BASE_URL}/repairs/${id}`, data);
export const deleteRepairRequest = (id) => axios.delete(`${BASE_URL}/repairs/${id}`);
export const updateRequestStatus = (id, data) => axios.put(`${BASE_URL}/repairs/status/${id}`, data);
export const customerDecision = (id, data) => axios.put(`${BASE_URL}/repairs/customer-decision/${id}`, data);
export const assignTechnician = (id, data) => axios.put(`${BASE_URL}/repairs/assign/${id}`, data);
export const updateProgress = (id, data) => axios.put(`${BASE_URL}/repairs/progress/${id}`, data);
export const getCustomerRepairRequests = (customerId) => axios.get(`${BASE_URL}/repairs/dashboard/customer/${customerId}`);
