import React, { useState } from "react";
import { createRepairRequest } from "../api/api";

const RepairRequestForm = () => {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    passwordHash: "defaultpassword", // temporary placeholder
    firstName: "",
    lastName: "",
    contactNumber: "",
    address: "",
    damageType: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // 1️⃣ Create User first
      const userRes = await fetch("http://localhost:5000/api/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          username: formData.username,
          email: formData.email,
          passwordHash: formData.passwordHash,
          firstName: formData.firstName,
          lastName: formData.lastName,
          contactNumber: formData.contactNumber,
          address: formData.address,
        }),
      });
      const newUser = await userRes.json();

      if (!newUser._id) {
        alert("Error creating user. Maybe username/email exists.");
        return;
      }

      // 2️⃣ Create Repair Request with newUser ID
      const repairRes = await createRepairRequest({
        customerId: newUser._id,
        damageType: formData.damageType,
      });

      alert("Repair request submitted successfully!");
      setFormData({
        username: "",
        email: "",
        passwordHash: "defaultpassword",
        firstName: "",
        lastName: "",
        contactNumber: "",
        address: "",
        damageType: ""
      });
      console.log(repairRes.data);
    } catch (err) {
      console.error(err);
      alert("Error submitting repair request");
    }
  };

  return (
    <div className="max-w-2xl mx-auto mt-10 p-8 bg-white rounded-xl shadow-lg">
      <h2 className="text-3xl font-bold mb-6 text-[#072679]">New Repair Request</h2>
      <form className="flex flex-col gap-4" onSubmit={handleSubmit}>
        {/* Customer Info */}
        <div className="grid grid-cols-2 gap-4">
          <div>
            <label className="block mb-1 font-semibold text-[#000000]">First Name</label>
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleChange}
              required
              className="w-full border border-gray-300 rounded px-3 py-2"
            />
          </div>
          <div>
            <label className="block mb-1 font-semibold text-[#000000]">Last Name</label>
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
              required
              className="w-full border border-gray-300 rounded px-3 py-2"
            />
          </div>
        </div>

        <div>
          <label className="block mb-1 font-semibold text-[#000000]">Username</label>
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={handleChange}
            required
            className="w-full border border-gray-300 rounded px-3 py-2"
          />
        </div>

        <div>
          <label className="block mb-1 font-semibold text-[#000000]">Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
            className="w-full border border-gray-300 rounded px-3 py-2"
          />
        </div>

        <div>
          <label className="block mb-1 font-semibold text-[#000000]">Contact Number</label>
          <input
            type="text"
            name="contactNumber"
            value={formData.contactNumber}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded px-3 py-2"
          />
        </div>

        <div>
          <label className="block mb-1 font-semibold text-[#000000]">Address</label>
          <textarea
            name="address"
            value={formData.address}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded px-3 py-2"
          ></textarea>
        </div>

        <div>
          <label className="block mb-1 font-semibold text-[#000000]">Damage Type</label>
          <input
            type="text"
            name="damageType"
            value={formData.damageType}
            onChange={handleChange}
            required
            className="w-full border border-gray-300 rounded px-3 py-2"
          />
        </div>

        <button
          type="submit"
          className="bg-[#42ADF5] hover:bg-[#2C8ED1] text-white font-bold py-2 px-4 rounded mt-4"
        >
          Submit Request
        </button>
      </form>
    </div>
  );
};

export default RepairRequestForm;
