import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getCustomerRepairRequests } from '../api/repairApi';
import RepairCard from '../components/RepairCard';

const CustomerDashboard = () => {
  const { customerId } = useParams();
  const [repairs, setRepairs] = useState([]);

  const fetchRepairs = async () => {
    try {
      const res = await getCustomerRepairRequests(customerId);
      setRepairs(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchRepairs();
  }, [customerId]);

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4 text-[#000000]">My Repair Requests</h2>
      {repairs.map((repair) => (
        <RepairCard key={repair._id} repair={repair} />
      ))}
    </div>
  );
};

export default CustomerDashboard;
