import React, { useEffect, useState } from "react";
import axios from "axios";

const RepairRequestForm = ({ customerId }) => {
  const [customer, setCustomer] = useState(null);
  const [damageType, setDamageType] = useState("");
  const [successMsg, setSuccessMsg] = useState("");

  useEffect(() => {
    // Fetch customer details by ID
    const fetchCustomer = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/users/${customerId}`);
        setCustomer(res.data);
      } catch (err) {
        console.error("Error fetching customer:", err);
      }
    };

    fetchCustomer();
  }, [customerId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!damageType) return alert("Please enter damage type");

    try {
      await axios.post("http://localhost:5000/api/repair", {
        customerId: customer._id, // Use existing user ID
        damageType,
      });
      setSuccessMsg("Repair request submitted successfully!");
      setDamageType("");
    } catch (err) {
      console.error("Error submitting request:", err);
      alert(err.response?.data?.error || "Error submitting request");
    }
  };

  if (!customer) return <div>Loading customer info...</div>;

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold text-[#072679] mb-4">Submit Repair Request</h2>

      <p><strong>Name:</strong> {customer.firstName} {customer.lastName}</p>
      <p><strong>Email:</strong> {customer.email}</p>
      <p><strong>Contact:</strong> {customer.contactNumber}</p>
      <p><strong>Address:</strong> {customer.address}</p>

      <form onSubmit={handleSubmit} className="mt-4">
        <label className="block mb-2 font-semibold text-[#36516C]">Damage Type</label>
        <input
          type="text"
          placeholder="Enter damage type"
          value={damageType}
          onChange={(e) => setDamageType(e.target.value)}
          className="w-full px-3 py-2 mb-4 border rounded-md focus:outline-none focus:ring-2 focus:ring-[#42ADF5]"
        />

        <button
          type="submit"
          className="w-full bg-[#42ADF5] hover:bg-[#2C8ED1] text-white font-bold py-2 px-4 rounded-md"
        >
          Submit Request
        </button>

        {successMsg && <p className="mt-2 text-green-600">{successMsg}</p>}
      </form>
    </div>
  );
};

export default RepairRequestForm;
