import React, { useState, useEffect } from 'react';
import { createRepairRequest, getAllRepairRequests } from '../api/repairApi';

const RepairRequestForm = ({ customerId }) => {
  const [damageType, setDamageType] = useState('');
  const [requests, setRequests] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createRepairRequest({ customerId, damageType });
      alert('Repair request submitted successfully');
      setDamageType('');
      fetchRequests();
    } catch (err) {
      alert(err.response?.data?.error || err.message);
    }
  };

  const fetchRequests = async () => {
    const res = await getAllRepairRequests({ customerId });
    setRequests(res.data);
  };

  useEffect(() => {
    fetchRequests();
  }, []);

  return (
    <div className="p-6 bg-brand.light rounded shadow-md max-w-xl mx-auto">
      <h2 className="text-xl font-bold text-brand.heading mb-4">Submit Repair Request</h2>
      <form onSubmit={handleSubmit} className="flex flex-col gap-4">
        <input
          type="text"
          placeholder="Damage Type"
          value={damageType}
          onChange={(e) => setDamageType(e.target.value)}
          className="p-2 border border-gray-300 rounded"
          required
        />
        <button className="bg-brand.secondary hover:bg-brand.hoverBlue text-white px-4 py-2 rounded">
          Submit
        </button>
      </form>

      <h3 className="mt-6 text-lg font-semibold text-brand.heading">My Requests</h3>
      {requests.map((r) => (
        <div key={r._id} className="bg-white p-3 rounded shadow mt-2">
          <p className="text-brand.body">Damage: {r.damageType}</p>
          <p className="text-brand.body">Status: {r.status}</p>
          <p className="text-brand.body">Stage: {r.currentStage}</p>
        </div>
      ))}
    </div>
  );
};

export default RepairRequestForm;
