import React, { useState, useEffect } from 'react';
import { createRepairRequest, getCustomerRepairRequests } from '../api/repairApi';
import CustomerDashboard from './CustomerDashboard';

const RepairForm = ({ customer }) => {
  const [damageType, setDamageType] = useState('');
  const [firstName, setFirstName] = useState(customer?.firstName || '');
  const [lastName, setLastName] = useState(customer?.lastName || '');
  const [email, setEmail] = useState(customer?.email || '');
  const [contactNumber, setContactNumber] = useState(customer?.contactNumber || '');
  const [requests, setRequests] = useState([]);
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // submit form
      await createRepairRequest({
        customerId: customer._id,
        damageType
      });
      alert('Repair request submitted successfully');
      setDamageType('');
      fetchRequests();
      setSubmitted(true);
    } catch (err) {
      alert(err.response?.data?.error || err.message);
    }
  };

  const fetchRequests = async () => {
    const res = await getCustomerRepairRequests(customer._id);
    setRequests(res.data);
  };

  useEffect(() => {
    if (customer?._id) fetchRequests();
  }, [customer]);

  if (submitted) {
    return <CustomerDashboard requests={requests} />;
  }

  return (
    <div className="p-6 max-w-xl mx-auto bg-white rounded shadow-md mt-6">
      <h2 className="text-2xl font-bold text-[#000000] mb-4">Submit Repair Request</h2>
      <form onSubmit={handleSubmit} className="flex flex-col gap-4">

        <input
          type="text"
          placeholder="First Name"
          value={firstName}
          readOnly
          className="p-2 border border-gray-300 rounded bg-gray-100"
        />

        <input
          type="text"
          placeholder="Last Name"
          value={lastName}
          readOnly
          className="p-2 border border-gray-300 rounded bg-gray-100"
        />

        <input
          type="email"
          placeholder="Email"
          value={email}
          readOnly
          className="p-2 border border-gray-300 rounded bg-gray-100"
        />

        <input
          type="text"
          placeholder="Contact Number"
          value={contactNumber}
          readOnly
          className="p-2 border border-gray-300 rounded bg-gray-100"
        />

        <input
          type="text"
          placeholder="Damage Type"
          value={damageType}
          onChange={(e) => setDamageType(e.target.value)}
          className="p-2 border border-gray-300 rounded"
          required
        />

        <button
          type="submit"
          className="bg-[#42ADF5] hover:bg-[#2C8ED1] text-white px-4 py-2 rounded font-medium"
        >
          Submit Request
        </button>
      </form>
    </div>
  );
};

export default RepairForm;
