import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createRepairRequest } from '../api/repairApi';

const RepairForm = ({ customer }) => {
  const navigate = useNavigate();
  const [damageType, setDamageType] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createRepairRequest({
        customerId: customer._id,
        damageType
      });
      alert('Repair request submitted!');
      navigate(`/customer-dashboard/${customer._id}`);
    } catch (err) {
      alert(err.response?.data?.error || err.message);
    }
  };

  return (
    <div className="p-6 bg-[#F1F2F7] rounded shadow-md max-w-xl mx-auto mt-8">
      <h2 className="text-xl font-bold mb-4 text-[#000000]">Submit Repair Request</h2>
      <form onSubmit={handleSubmit} className="flex flex-col gap-4">
        <input
          type="text"
          placeholder="Damage Type"
          value={damageType}
          onChange={(e) => setDamageType(e.target.value)}
          className="p-2 border border-gray-300 rounded"
          required
        />
        <button type="submit" className="bg-[#42ADF5] hover:bg-[#2C8ED1] text-white px-4 py-2 rounded">Submit</button>
      </form>
    </div>
  );
};

export default RepairForm;
