import React, { useState, useEffect } from 'react';
import { createFeedback, getAllFeedbacks } from '../api/repairApi';
import FeedbackCard from '../components/FeedbackCard';

const FeedbackForm = ({ customerId }) => {
  const [message, setMessage] = useState('');
  const [feedbacks, setFeedbacks] = useState([]);

  const fetchFeedbacks = async () => {
    const res = await getAllFeedbacks();
    setFeedbacks(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await createFeedback({ customerId, message });
    setMessage('');
    fetchFeedbacks();
  };

  useEffect(() => { fetchFeedbacks(); }, []);

  return (
    <div className="p-6 bg-brand.light rounded shadow-md max-w-xl mx-auto">
      <form onSubmit={handleSubmit} className="flex flex-col gap-4">
        <textarea
          placeholder="Your feedback"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          className="p-2 border border-gray-300 rounded"
          required
        />
        <button className="bg-brand.secondary hover:bg-brand.hoverBlue text-white px-4 py-2 rounded">
          Submit Feedback
        </button>
      </form>

      <h3 className="mt-6 text-lg font-semibold text-brand.heading">Feedbacks</h3>
      {feedbacks.map(f => <FeedbackCard key={f._id} feedback={f} />)}
    </div>
  );
};

export default FeedbackForm;
