import React, { useEffect, useState } from 'react';
import RepairForm from '../components/RepairForm';
import RepairList from '../components/RepairList';
import {
  getCustomerRepairs,
  deleteRepairRequest,
  downloadRepairReport,
} from '../api/repairApi';
import Navbar from '../components/Navbar';

const RepairDashboard = () => {
  const customerId = 'YOUR_CUSTOMER_ID_HERE'; // Replace with dynamic ID if using auth
  const [repairs, setRepairs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const fetchRepairs = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await getCustomerRepairs(customerId);
      setRepairs(res.data);
    } catch (err) {
      console.error(err);
      setError('Failed to fetch repair requests.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRepairs();
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this request?')) {
      try {
        await deleteRepairRequest(id);
        fetchRepairs();
      } catch (err) {
        alert('Failed to delete repair request.');
      }
    }
  };

  const handleDownload = async (id) => {
    try {
      const res = await downloadRepairReport(id);
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `repair_report_${id}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      alert('Failed to download report.');
    }
  };

  return (
    <div className="min-h-screen bg-lightBg">
      <Navbar />
      <div className="max-w-4xl mx-auto p-4 space-y-6">
        <RepairForm customerId={customerId} onSuccess={fetchRepairs} />

        <h2 className="text-primary font-bold text-xl">My Repair Requests</h2>

        {loading ? (
          <p className="text-body">Loading repair requests...</p>
        ) : error ? (
          <p className="text-red-500">{error}</p>
        ) : repairs.length === 0 ? (
          <p className="text-body">No repair requests found.</p>
        ) : (
          <RepairList
            repairs={repairs}
            onDelete={handleDelete}
            onDownload={handleDownload}
            onUpdate={() => alert('Update functionality can open a modal or page')}
          />
        )}
      </div>
    </div>
  );
};

export default RepairDashboard;
