import React, { useState } from "react";
import { createRepairRequest } from "../api/repairRequestApi";

const RepairForm = ({ onRequestCreated }) => {
  const [formData, setFormData] = useState({
    customerName: "",
    customerEmail: "",
    customerPhone: "",
    damageType: "",
    description: "",
    status: "Pending",
  });

  // Handle input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createRepairRequest(formData);
      alert("Repair request submitted successfully ✅");
      setFormData({
        customerName: "",
        customerEmail: "",
        customerPhone: "",
        damageType: "",
        description: "",
        status: "Pending",
      });
      onRequestCreated(); // refresh list
    } catch (error) {
      console.error("Error submitting request:", error);
      alert("❌ Failed to submit repair request. Check backend logs.");
    }
  };

  return (
    <div className="p-6 bg-white rounded-lg shadow-md max-w-lg mx-auto">
      <h2 className="text-2xl font-semibold mb-4 text-center">
        Submit Repair Request
      </h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Customer Info */}
        <input
          type="text"
          name="customerName"
          placeholder="Customer Name"
          value={formData.customerName}
          onChange={handleChange}
          className="w-full p-2 border rounded"
          required
        />
        <input
          type="email"
          name="customerEmail"
          placeholder="Customer Email"
          value={formData.customerEmail}
          onChange={handleChange}
          className="w-full p-2 border rounded"
          required
        />
        <input
          type="text"
          name="customerPhone"
          placeholder="Customer Phone"
          value={formData.customerPhone}
          onChange={handleChange}
          className="w-full p-2 border rounded"
          required
        />

        {/* Repair Info */}
        <select
          name="damageType"
          value={formData.damageType}
          onChange={handleChange}
          className="w-full p-2 border rounded"
          required
        >
          <option value="">Select Damage Type</option>
          <option value="Screen">Screen</option>
          <option value="Battery">Battery</option>
          <option value="Software">Software</option>
          <option value="Other">Other</option>
        </select>

        <textarea
          name="description"
          placeholder="Problem Description"
          value={formData.description}
          onChange={handleChange}
          className="w-full p-2 border rounded"
          required
        ></textarea>

        <button
          type="submit"
          className="w-full bg-blue-600 text-white p-2 rounded hover:bg-blue-700"
        >
          Submit Request
        </button>
      </form>
    </div>
  );
};

export default RepairForm;
