import React, { useState } from 'react';
import { createRepairRequest } from '../api/repairApi';

const RepairForm = ({ customerId, onSuccess }) => {
  const [damageType, setDamageType] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await createRepairRequest({ customerId, damageType });
      setDamageType('');
      onSuccess();
    } catch (err) {
      alert(err.response?.data?.error || 'Error creating request');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="bg-lightBg p-6 rounded shadow space-y-4">
      <h2 className="text-primary font-bold text-xl">New Repair Request</h2>
      <input
        type="text"
        placeholder="Damage Type"
        value={damageType}
        onChange={(e) => setDamageType(e.target.value)}
        className="w-full p-2 border border-body rounded"
        required
      />
      <button
        type="submit"
        disabled={loading}
        className="bg-secondary text-white px-4 py-2 rounded hover:bg-hoverBlue"
      >
        {loading ? 'Submitting...' : 'Submit Request'}
      </button>
    </form>
  );
};

export default RepairForm;
