import React, { useState } from "react";
import { createRepairRequest } from "../api/repairApi";

const RepairForm = ({ customerId, onSuccess }) => {
  const [damageType, setDamageType] = useState("");
  const [loading, setLoading] = useState(false);

  const submit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await createRepairRequest({ customerId, damageType });
      setDamageType("");
      onSuccess?.();
    } catch (e) {
      alert(e.response?.data?.error || "Error creating request");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form
      onSubmit={submit}
      className="bg-white p-4 rounded-2xl shadow flex gap-3 items-center"
    >
      <input
        className="flex-1 border rounded-lg p-2"
        placeholder="Damage Type (e.g., Crack near handle)"
        value={damageType}
        onChange={(e) => setDamageType(e.target.value)}
        required
      />
      <button
        type="submit"
        disabled={loading}
        className="bg-secondary hover:bg-hoverBlue text-white px-4 py-2 rounded-lg"
      >
        {loading ? "Submitting..." : "Submit"}
      </button>
    </form>
  );
};

export default RepairForm;
