import axios from "axios";

const BASE_URL = "http://localhost:5000/api/repairs"; 

export const submitRepairRequest = async (data) => {
  return await axios.post(`${BASE_URL}`, data);
};

// In repairRequestRoutes.js
router.get("/dashboard/username/:username", async (req, res) => {
  try {
    const requests = await RepairRequest.find({ username: req.params.username });
    res.json(requests);
  } catch (err) {
    res.status(500).json({ error: err.message });
  }
});

// ✅ Add this function for fetching by username
export const getCustomerRequestsByUsername = async (username) => {
  return await axios.get(`${BASE_URL}/dashboard/username/${username}`);
};

export const updateRepairRequest = async (id, data) => {
  return await axios.put(`${BASE_URL}/${id}`, data);
};

export const deleteRepairRequest = async (id) => {
  return await axios.delete(`${BASE_URL}/${id}`);
};

export const downloadRepairPDF = async (id) => {
  return await axios.get(`${BASE_URL}/report/download/${id}`, { responseType: "blob" });
};
