import React from 'react';

const RepairRequestCard = ({ request }) => {
  return (
    <div className="bg-brand.light p-4 rounded shadow mb-4">
      <h3 className="text-lg font-bold text-brand.heading">Damage Type: {request.damageType}</h3>
      <p className="text-brand.body">Status: {request.status}</p>
      <p className="text-brand.body">Current Stage: {request.currentStage}</p>
      <p className="text-brand.body">Progress: {request.repairProgress}%</p>
    </div>
  );
};

export default RepairRequestCard;
