import axios from "axios";

const API_URL = "http://localhost:5000/api/repairs"; // adjust if different

export const createRepairRequest = (data) => axios.post(API_URL, data);
export const getCustomerRepairs = (customerId) =>
  axios.get(`${API_URL}/dashboard/customer/${customerId}`);
export const deleteRepairRequest = (id) => axios.delete(`${API_URL}/${id}`);
export const updateRepairRequest = (id, data) =>
  axios.put(`${API_URL}/${id}`, data);
export const downloadRepairReport = (id) =>
  axios.get(`${API_URL}/report/download/${id}`, { responseType: "blob" });
