import axios from 'axios';

const BASE_URL = 'http://localhost:5000/api/repairs'; 

// Create repair request
export const createRepairRequest = (data) => axios.post(`${BASE_URL}/`, data);

// Get all repair requests for customer
export const getCustomerRepairs = (customerId) => axios.get(`${BASE_URL}/dashboard/customer/${customerId}`);

// Update repair request
export const updateRepairRequest = (id, data) => axios.put(`${BASE_URL}/${id}`, data);

// Delete repair request
export const deleteRepairRequest = (id) => axios.delete(`${BASE_URL}/${id}`);

// Download PDF
export const downloadRepairReport = (id) => {
  return axios.get(`${BASE_URL}/report/download/${id}`, { responseType: 'blob' });
};
