import React from 'react';

const RepairCard = ({ repair }) => {
  return (
    <div className="bg-white p-4 rounded shadow mb-4">
      <p><strong>Damage:</strong> {repair.damageType}</p>
      <p><strong>Status:</strong> {repair.status}</p>
      <p><strong>Stage:</strong> {repair.currentStage}</p>
      <p><strong>Cost Estimate:</strong> {repair.costEstimate || 'N/A'}</p>
      <p><strong>Time Estimate:</strong> {repair.timeEstimate || 'N/A'}</p>
      <p><strong>Assigned Technician:</strong> {repair.assignedTechnician?.username || 'N/A'}</p>
    </div>
  );
};

export default RepairCard;
