import React from "react";
import ProgressBar from "./ProgressBar";

const RepairList = ({
  repairs,
  onDelete,
  onDownload,
  onApprove,
  onReject,
  onUpdate,
}) => {
  if (!repairs?.length)
    return <p className="text-body">No repair requests yet.</p>;

  return (
    <div className="space-y-4">
      {repairs.map((r) => (
        <div key={r._id} className="bg-white rounded-2xl shadow p-4">
          <div className="flex flex-wrap justify-between gap-2">
            <div>
              <p className="font-semibold text-black">{r.damageType}</p>
              <p className="text-sm text-body">Status: {r.status}</p>
              {r.currentStage && (
                <p className="text-xs text-body/80">Stage: {r.currentStage}</p>
              )}
              <div className="mt-2">
                <ProgressBar value={r.repairProgress || 0} />
              </div>
            </div>
            <div className="flex items-center gap-2">
              <button
                onClick={() => onUpdate?.(r._id)}
                className="px-3 py-1 rounded bg-accent text-white"
              >
                Update
              </button>
              <button
                onClick={() => onDelete?.(r._id)}
                className="px-3 py-1 rounded bg-red-500 text-white"
              >
                Delete
              </button>
              <button
                onClick={() => onDownload?.(r._id)}
                className="px-3 py-1 rounded bg-primary text-white"
              >
                PDF
              </button>
            </div>
          </div>

          {/* Customer approval controls if waiting */}
          {r.status === "Customer Approved" || r.status === "Customer Rejected" ? null : (
            r.currentStage === "Waiting for Customer Approval" && (
              <div className="mt-3 flex items-center gap-2">
                <span className="text-sm">
                  Estimate:{" "}
                  <b>{r.costEstimate ?? "-"}</b> | Time:{" "}
                  <b>{r.timeEstimate ?? "-"}</b>
                </span>
                <button
                  onClick={() => onApprove?.(r._id)}
                  className="px-3 py-1 rounded bg-green-600 text-white"
                >
                  Approve
                </button>
                <button
                  onClick={() => onReject?.(r._id)}
                  className="px-3 py-1 rounded bg-red-600 text-white"
                >
                  Reject
                </button>
              </div>
            )
          )}
        </div>
      ))}
    </div>
  );
};

export default RepairList;
