import axios from "axios";

const API = axios.create({
  baseURL: "http://localhost:5000/api",
});

// Users
export const getUsers = () => API.get("/users");




// Repair Requests
export const getCustomerRepairRequests = (customerId) =>
  axios.get(`http://localhost:5000/api/repair-requests/dashboard/customer/${customerId}`);

export const createRepairRequest = (data) =>
  axios.post("http://localhost:5000/api/repair-requests", data);

export const updateRepairRequest = (id, data) =>
  axios.put(`http://localhost:5000/api/repair-requests/${id}`, data);

export const deleteRepairRequest = (id) =>
  axios.delete(`http://localhost:5000/api/repair-requests/${id}`);
