import React, { useEffect, useState } from "react";
import axios from "axios";

const CustomerDashboard = ({ loggedInUser }) => {
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchRequests = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/repairRequests/dashboard/customer/${loggedInUser._id}`);
      setRequests(res.data);
    } catch (err) {
      console.error(err);
    }
    setLoading(false);
  };

  const deleteRequest = async (id) => {
    if (!window.confirm("Are you sure you want to delete this request?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/repairRequests/${id}`);
      fetchRequests();
    } catch (err) {
      alert("Failed to delete request");
    }
  };

  const downloadPDF = (id) => {
    window.open(`http://localhost:5000/api/repairRequests/report/download/${id}`, "_blank");
  };

  useEffect(() => {
    fetchRequests();
  }, []);

  if (loading) return <p className="text-center mt-10">Loading...</p>;

  return (
    <div className="max-w-4xl mx-auto mt-10">
      <h1 className="text-3xl font-bold text-primary mb-6">My Repair Requests</h1>
      {requests.length === 0 && <p>No repair requests submitted yet.</p>}

      {requests.map((req) => (
        <div key={req._id} className="bg-white p-4 rounded-xl shadow mb-4">
          <h2 className="font-bold text-lg text-secondary mb-2">{req.damageType}</h2>
          <p><span className="font-semibold">Status:</span> {req.status}</p>
          <p><span className="font-semibold">Progress:</span> {req.repairProgress}%</p>
          <p><span className="font-semibold">Current Stage:</span> {req.currentStage || "-"}</p>
          <p><span className="font-semibold">Cost Estimate:</span> {req.costEstimate ?? "-"}</p>
          <p><span className="font-semibold">Time Estimate:</span> {req.timeEstimate ?? "-"}</p>
          <div className="mt-2 flex space-x-2">
            <button onClick={() => deleteRequest(req._id)}
              className="bg-red-500 hover:bg-red-600 text-white px-4 py-1 rounded">Delete</button>
            <button onClick={() => downloadPDF(req._id)}
              className="bg-secondary hover:bg-buttonHover text-white px-4 py-1 rounded">Download PDF</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default CustomerDashboard;
