import axios from "axios";

const API_URL = "http://localhost:5000/api/repair-requests";

// ✅ Get all repair requests
export const getRepairRequests = async () => {
  return await axios.get(API_URL);
};

// ✅ Create a new repair request (includes customer info)
export const createRepairRequest = async (requestData) => {
  return await axios.post(API_URL, requestData);
};

// ✅ Update repair request
export const updateRepairRequest = async (id, requestData) => {
  return await axios.put(`${API_URL}/${id}`, requestData);
};

// ✅ Delete repair request
export const deleteRepairRequest = async (id) => {
  return await axios.delete(`${API_URL}/${id}`);
};
