import React, { useEffect, useState } from "react";
import { getCustomerRepairRequests } from "../api/api";

const CustomerDashboard = () => {
  const [requests, setRequests] = useState([]);
  const customerId = "YOUR_CUSTOMER_ID_HERE"; // Replace with logged-in customer ID

  useEffect(() => {
    getCustomerRepairRequests(customerId).then(res => setRequests(res.data));
  }, [customerId]);

  return (
    <div className="max-w-4xl mx-auto mt-10">
      <h2 className="text-2xl font-bold text-primary mb-4">Your Repair Requests</h2>
      {requests.length === 0 ? (
        <p>No repair requests found.</p>
      ) : (
        <div className="space-y-4">
          {requests.map((req) => (
            <div key={req._id} className="bg-white p-4 rounded-xl shadow-md">
              <p><strong>Damage:</strong> {req.damageType}</p>
              <p><strong>Status:</strong> {req.status}</p>
              <p><strong>Progress:</strong> {req.repairProgress}%</p>
              <p><strong>Technician:</strong> {req.assignedTechnician?.technicianId?.username || "Not assigned"}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default CustomerDashboard;
