import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import RepairForm from './pages/RepairForm';
import CustomerDashboard from './pages/CustomerDashboard';
import FeedbackPage from './pages/FeedbackPage';

const App = () => {
  // Simulate logged-in customer (replace with actual auth later)
  const customer = { _id: '64e123abc456def7890', username: 'John Doe' };

  return (
    <Router>
      <Navbar />
      <Routes>
        <Route path="/repair-form" element={<RepairForm customer={customer} />} />
        <Route path="/customer-dashboard/:customerId" element={<CustomerDashboard />} />
        <Route path="/feedback" element={<FeedbackPage customer={customer} />} />
        <Route path="*" element={<RepairForm customer={customer} />} />
      </Routes>
    </Router>
  );
};

export default App;
