import React from "react";
import { Link, useLocation } from "react-router-dom";

const NavLink = ({ to, label }) => {
  const loc = useLocation();
  const active = loc.pathname === to;
  return (
    <Link
      to={to}
      className={`px-3 py-2 rounded-lg text-sm font-semibold ${
        active ? "bg-secondary text-white" : "text-white hover:bg-hoverBlue"
      }`}
    >
      {label}
    </Link>
  );
};

const Navbar = () => {
  return (
    <nav className="bg-primary text-white px-6 py-3 flex items-center justify-between shadow">
      <h1 className="font-bold text-lg">CricketXpert â€“ Repairs</h1>
      <div className="flex gap-2">
        <NavLink to="/" label="Customer" />
        <NavLink to="/manager" label="Service Manager" />
        <NavLink to="/technician" label="Technician" />
        <NavLink to="/feedback" label="Feedback" />
      </div>
    </nav>
  );
};

export default Navbar;
