import React, { useEffect, useState } from 'react';
import { getAllRepairRequests } from '../api/repairApi';
import RepairRequestCard from '../components/RepairRequestCard';

const DashboardCustomer = ({ customerId }) => {
  const [requests, setRequests] = useState([]);

  const fetchRequests = async () => {
    const res = await getAllRepairRequests({ customerId });
    setRequests(res.data);
  };

  useEffect(() => {
    fetchRequests();
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold text-brand.heading mb-4">My Repair Requests</h2>
      {requests.map((r) => (
        <RepairRequestCard key={r._id} request={r} />
      ))}
    </div>
  );
};

export default DashboardCustomer;
