import { useEffect, useState } from "react";
import {
  getCustomerRequestsByUsername,
  deleteRepairRequest,
  updateRepairRequest,
  downloadRepairPDF,
} from "../api/repairRequestApi";

const RepairRequestCard = ({ request, onDelete, onUpdate, onDownload }) => {
  return (
    <div className="bg-lightBg p-4 rounded-xl shadow mb-4">
      <h3 className="text-xl font-bold text-primary mb-2">{request.damageType}</h3>
      <p><strong>Status:</strong> {request.status}</p>
      <p><strong>Progress:</strong> {request.repairProgress || 0}%</p>
      <p><strong>Current Stage:</strong> {request.currentStage || "N/A"}</p>
      {request.costEstimate && <p><strong>Cost:</strong> {request.costEstimate}</p>}
      {request.timeEstimate && <p><strong>Time Estimate:</strong> {request.timeEstimate}</p>}
      <div className="mt-2 flex gap-2">
        <button
          onClick={() => onUpdate(request)}
          className="bg-secondaryBtn text-white py-1 px-3 rounded hover:bg-secondaryBtnHover"
        >
          Update
        </button>
        <button
          onClick={() => onDelete(request._id)}
          className="bg-primary text-white py-1 px-3 rounded hover:bg-primaryHover"
        >
          Delete
        </button>
        <button
          onClick={() => onDownload(request._id)}
          className="bg-secondary text-white py-1 px-3 rounded hover:bg-primaryHover"
        >
          Download PDF
        </button>
      </div>
    </div>
  );
};

const CustomerDashboard = ({ username }) => {
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchRequests = async () => {
    setLoading(true);
    try {
      const res = await getCustomerRequestsByUsername(username);
      setRequests(res.data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this request?")) {
      await deleteRepairRequest(id);
      fetchRequests();
    }
  };

  const handleUpdate = async (request) => {
    const newDamageType = prompt("Enter new damage type:", request.damageType);
    if (!newDamageType) return;
    await updateRepairRequest(request._id, { damageType: newDamageType });
    fetchRequests();
  };

  const handleDownload = async (id) => {
    const res = await downloadRepairPDF(id);
    const url = window.URL.createObjectURL(new Blob([res.data]));
    const link = document.createElement("a");
    link.href = url;
    link.setAttribute("download", `repair_report_${id}.pdf`);
    document.body.appendChild(link);
    link.click();
  };

  useEffect(() => {
    if (username) fetchRequests();
  }, [username]);

  if (loading) return <p className="text-body mt-4">Loading repair requests...</p>;

  return (
    <div className="max-w-3xl mx-auto mt-10">
      <h2 className="text-2xl font-bold text-primary mb-6">My Repair Requests</h2>
      {requests.length === 0 ? (
        <p className="text-body">No repair requests submitted yet.</p>
      ) : (
        requests.map((req) => (
          <RepairRequestCard
            key={req._id}
            request={req}
            onDelete={handleDelete}
            onUpdate={handleUpdate}
            onDownload={handleDownload}
          />
        ))
      )}
    </div>
  );
};

export default CustomerDashboard;
