import http from "./http";

// Repairs base
const REPAIRS = "/api/repairs";

// Customer
export const createRepairRequest = (data) => http.post(`${REPAIRS}`, data);
export const getCustomerRepairs = (customerId) =>
  http.get(`${REPAIRS}/dashboard/customer/${customerId}`);

// Technician
export const getTechnicianRepairs = (technicianId, status) =>
  http.get(`${REPAIRS}/dashboard/technician/${technicianId}`, {
    params: status ? { status } : {},
  });
export const updateProgress = (id, repairProgress) =>
  http.put(`${REPAIRS}/progress/${id}`, { repairProgress });

// Manager
export const getAllRepairsManager = (filters) =>
  http.get(`${REPAIRS}/dashboard/manager`, { params: filters || {} });
export const updateRequestStatus = (id, payload) =>
  http.put(`${REPAIRS}/status/${id}`, payload);
export const assignTechnician = (id, technicianId) =>
  http.put(`${REPAIRS}/assign/${id}`, { technicianId });

// Common
export const updateRepairRequest = (id, data) =>
  http.put(`${REPAIRS}/${id}`, data);
export const deleteRepairRequest = (id) => http.delete(`${REPAIRS}/${id}`);
export const getRepairById = (id) => http.get(`${REPAIRS}/${id}`);
export const downloadRepairReport = (id) =>
  http.get(`${REPAIRS}/report/download/${id}`, { responseType: "blob" });

// Customer decision
export const customerDecision = (id, decision) =>
  http.put(`${REPAIRS}/customer-decision/${id}`, { decision });
